mapper.insert:
insert into ssafy_member (
				  	userid
				  , userpwd
				  , username
			      , address
				  , email
				  ) 
values (#{userid}
	  , #{userpwd}
	  , #{username}
	  , #{address}
	  , #{email}
	  )

mapper.select:
select *
from   ssafy_member
where  userid = #{userid}
and	   userpwd = #{userpwd}

mapper.update:
update ssafy_member set
	  	userpwd = #{userpwd}
	  , username = #{username}
	  , address = #{address}
	  , email = #{email}
where 	userid= #{userid}
